/**
 * Defines the options that can be used for the `gridAlign` group helper
 * function.
 */
export interface GridAlignOptions {
    paddingBetweenRows: number;
    paddingBetweenColumns: number;
}
/**
 * Defines the options that can be used for the `placeOnCircle` group helper function.
 */
export interface CirclePlacementOptions {
    rotateItems: boolean;
    rotateItemsInverse: boolean;
}
